mapper.update:
update product_order
set status = #{status}
where id = #{id}

mapper.select:
select po.id, ci.phone, ci.name customerName, product.product_name productName, product.price, po.quantity, po.order_date orderDate, po.status from product_order po left join customer_info ci on po.customer_id = ci.id left join product on po.product_id = product.id
AND product.product_name like concat('%', #{productName}, '%')
AND DATE(po.order_date) = #{orderTime}
AND po.status = #{status}
order by po.order_date desc
